#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\000Projects\JAVA\emp-train\emp-train-web\node_modules\.pnpm\svgo@2.8.0\node_modules\svgo\bin\node_modules;D:\000Projects\JAVA\emp-train\emp-train-web\node_modules\.pnpm\svgo@2.8.0\node_modules\svgo\node_modules;D:\000Projects\JAVA\emp-train\emp-train-web\node_modules\.pnpm\svgo@2.8.0\node_modules;D:\000Projects\JAVA\emp-train\emp-train-web\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/000Projects/JAVA/emp-train/emp-train-web/node_modules/.pnpm/svgo@2.8.0/node_modules/svgo/bin/node_modules:/mnt/d/000Projects/JAVA/emp-train/emp-train-web/node_modules/.pnpm/svgo@2.8.0/node_modules/svgo/node_modules:/mnt/d/000Projects/JAVA/emp-train/emp-train-web/node_modules/.pnpm/svgo@2.8.0/node_modules:/mnt/d/000Projects/JAVA/emp-train/emp-train-web/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../svgo/bin/svgo" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../svgo/bin/svgo" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../svgo/bin/svgo" $args
  } else {
    & "node$exe"  "$basedir/../svgo/bin/svgo" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
